mapper.select:
SELECT
    SUM(tran_inc_cost) AS amount,
    FROM_UNIXTIME(tran_inc_datetime,'%b') months
FROM
    TransactionIncome
WHERE
    tran_inc_datetime >= #{startTime}
  AND tran_inc_datetime <= #{endTime}
  AND user_id = #{userId}
GROUP BY
    months;

SELECT
    SUM(tran_inc_cost),
    FROM_UNIXTIME(tran_inc_datetime,'%b') months
FROM
    TransactionOutcome
WHERE
    tran_inc_datetime >= #{startTime}
    AND tran_inc_datetime <= #{endTime}
    AND user_id = #{userId}
GROUP BY
    #{interval};
